CREATE TABLE IF NOT EXISTS exchange_rates (id BIGINT AUTO_INCREMENT PRIMARY KEY,record_date DATE NOT NULL,currency VARCHAR(255) NOT NULL,exchange_rate DECIMAL(15,2) NOT NULL);
CREATE TABLE IF NOT EXISTS transactions (id BIGINT AUTO_INCREMENT PRIMARY KEY,record_date DATE NOT NULL,description VARCHAR(50) NOT NULL,amount DECIMAL(15,2) NOT NULL);
CREATE TABLE IF NOT EXISTS user (id BIGINT AUTO_INCREMENT PRIMARY KEY,username varchar(50) NOT NULL,password varchar(50) NOT NULL,email varchar(50) NOT NULL,active boolean NOT NULL,roles varchar(50) NOT NULL);